mapper.update:
update board_data
set etc_title=#{etc_title},
etc_context=#{etc_context}
where etc_NO=#{etc_NO}

choose.when:
update data_file
set up_fileName = #{item.up_fileName}
where
etc_NO=#{item.etc_NO}
and up_fileNO = #{item.up_fileNO}

mapper.delete:

delete from data_file
where etc_NO = #{etc_NO}
and up_fileNO = #{up_fileNO}

mapper.update:
UPDATE board_data SET
etc_hits = etc_hits + 1
WHERE etc_NO = #{etc_NO}
