mapper.update:
update board_Notice
set
noti_title=#{noti_title},
noti_context=#{noti_context}
where
noti_NO=#{noti_NO}

choose.when:
update noti_file
set up_fileName =
#{item.up_fileName}
where
noti_NO=#{item.noti_NO}
and up_fileNO =
#{item.up_fileNO}

mapper.delete:

delete from noti_file
where noti_NO = #{noti_NO}
and up_fileNO = #{up_fileNO}

mapper.update:
UPDATE board_Notice SET
noti_hits = noti_hits+1
WHERE noti_NO = #{noti_NO}
