mapper.select:
select decode(count(*),1, 'true', 0, 'false')
from member
where member_id = #{member_id}


select * from member 
 where member_id=#{member_id}
 and member_pw=#{member_pw}

mapper.update:

update member
set pwd=#{member_pw}, name=#{member_name}, email=#{member_email1}
where
id=#{member_id}
