mapper.update:
update board_free
set fr_title=#{fr_title},
	fr_context=#{fr_context}
where fr_NO=#{fr_NO}

choose.when:
update data_file
set up_fileName = #{item.up_fileName}
where
fr_NO=#{item.fr_NO}
and up_fileNO = #{item.up_fileNO}

mapper.delete:

delete from free_file
where fr_NO = #{fr_NO}
and up_fileNO = #{up_fileNO}

mapper.update:
UPDATE board_free SET
fr_hits = fr_hits + 1
WHERE fr_NO = #{fr_NO}
